def setup_game():
    print("Welcome to Lingo!")
    while True:
        print("What size word do you want to play with, 4 or 5?")
        num_letters = input()
        try:
            answer = logic.pick_word(int(num_letters))
            return answer
        except logic.LingoException as err:
            print(err)

        except ValueError:
            print("Please enter an integer.")


def guess_at_answer(answer):
    print(answer[0] + "-" * (len(answer) - 1))
    while True:
        # TODO - change to number of rounds based on length of word
        print("Enter your guess:")
        guess = input()
        try:
            result = logic.check_guess(guess, answer)

            print(" ".join(result))
            if all(r == logic.CORRECT for r in result):
                print("You Win!")
                exit(0)
        except logic.LingoException as err:
            print(err)
            print("You Lose")
            print(ADIL)
            exit(1)


def main():
    answer = setup_game()
    guess_at_answer(answer)


if __name__ == "__main__":
    main()
